import React from 'react';

class SinglePet extends React.Component {
  constructor({pet}) {
    super(); 
    this.state = {
      /*
      id: pet.id, 
      name: pet.name, 
      description: pet.description,
      species: pet.species, 
      */
      pet: pet,
      adopted: false,
    }
    this.changeStatus = this.changeStatus.bind(this); 
  }

  changeStatus() {
    console.log(this.state.adopted); 
    this.setState({ 
      adopted: !this.state.adopted
    })
    let divElement = document.getElementById("statusChange");
    let element = document.getElementById("status"); 
    if (divElement.className === 'single-pet') {
      divElement.className = 'single-pet.adopted'; 
      element.innerText = 'Adopted!'; 
    } else {
      divElement.className = 'single-pet';
      element.innerText = 'Available';
    }

    console.log(divElement.classList); 


  }


  render() {
    return (
      <div id="statusChange" className="single-pet">
        <h1>Name: {this.state.pet.name}</h1>
        <h2>Description: {this.state.pet.description}</h2>
        <h3>Species: {this.state.pet.species}</h3>
        <h4 id="status">Available</h4>
        <button onClick={e => this.changeStatus(e)}>Toggle Status</button>

      </div>
    )
  }
}

export default SinglePet;

